<script>
    export let name = 'Carbon Dioxide'
    export let day = '26.05.2023'
    export let unit = 'ppm'
    export let data

    const minX = 47
    const maxX = 325
    const minY = 87
    const maxY = 187

    function getX(i) {
        return minX + (maxX - minX) / (data.length - 1) * i
    }
    let minValue, maxValue
    $: minValue = Math.min(...data.map(({ data }) => data))
    $: maxValue = Math.max(...data.map(({ data }) => data))
    function getY(value) {
        return minY + (maxY - minY) / (maxValue - minValue) * (value - minValue)
    }
    function getHour(timestamp) {
  const date = new Date(timestamp);
  const hours = date.getHours();
  const minutes = date.getMinutes();
  const formattedTime = `${hours.toString().padStart(2, '0')}:${minutes.toString().padStart(2, '0')}`;
  return formattedTime;
}

let selected = 0

const minSize = 50
const maxSize = 0

function getSize(distance) {
    // return 1 / (1 + Math.exp(-distance*2));
    let unnormalized = 1 - (maxSize - minSize) / (maxValue - minValue) * (distance - minValue) + 6
    //map from 0 to 1
    let nromalized = 1 / (1 + Math.exp(-unnormalized))
    return nromalized
}

</script>
