        <text x={getX(selected)} y="75" class="hover" fill="white">{data[selected].data} {unit}</text>
        <text x={getX(selected)} y="235" class="hover" fill="white">{getHour(data[selected].created_at)}</text>

        
        {#each data as _, i}
            {#if i == selected}
            <line x1={getX(i)} y1={minY} x2={getX(i)} y2={maxY} opacity="0.5" style="stroke:white;stroke-width:2" />

                <circle cx={getX(i)} cy={getY(data[i].data)} r="2.5" fill="var(--accent)"/>
            {:else}
                <circle cx={getX(i)} cy={getY(data[i].data)} r="2.5" fill="white"/>
            {/if}
            {#if i != data.length - 1}
                <line x1={getX(i)} y1={getY(data[i].data)} x2={getX(i+1)} y2={getY(data[i+1].data)} style="stroke:white;stroke-width:2" />
            {/if}
            {#if i%21 == 0}
            <text x={getX(i)} y="220" class="small" fill="white" style="font-size: { getSize(Math.abs((getX(i) - getX(selected)))) }rem;">{getHour(data[i].created_at)}</text>
            
            <!-- <circle cx={getX(i)} cy="200" r="2.5" fill="white"/> --> 
            <!-- {getHour(data[i].created_at)} -->
            {/if}
        {/each}
