<script>
    export let values = [65, 131, 89, 115, 160, 47, 193]
    const coords = [54, 94, 135, 176, 215, 258, 299]
    //get min and max value in values
    const min = Math.min(...values)
    const max = Math.max(...values)
    const average = values.reduce((a, b) => a + b, 0) / values.length
    export let mediumValue = (min + max) / 2
    function getPercentage(value){
        return (value - min) / (max - min) * 100
    }
    function getRange(percent){
        const minValue = 30
        const maxValue = 153

        const fullLength = maxValue - minValue
        const length = percent / 100 * fullLength
        const deltaLength = fullLength - length

        const min = minValue + deltaLength
        const max = maxValue - deltaLength

        return {
            min,
            max
        }
    }
    function get(value){
        return getRange(getPercentage(value))
    }
</script>

